PRAGMA defer_foreign_keys=TRUE;
CREATE TABLE IF NOT EXISTS "Post" (
    "slug" TEXT NOT NULL PRIMARY KEY,
    "title" TEXT NOT NULL,
    "excerpt" TEXT NOT NULL,
    "content" TEXT NOT NULL,
    "created_at" DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP,
    "updated_at" DATETIME NOT NULL,
    "author_id" TEXT,
    "author_name" TEXT,
    "author_avatar_url" TEXT,
    "image_url" TEXT,
    "image_hint" TEXT,
    "view_count" INTEGER NOT NULL DEFAULT 0,
    "image_preview_url" TEXT,
    "is_featured" BOOLEAN NOT NULL DEFAULT false,
    "image_feature_url" TEXT,
    "scheduled_at" DATETIME, "subhead" TEXT, "video_preview_url" TEXT,
    CONSTRAINT "Post_author_id_fkey" FOREIGN KEY ("author_id") REFERENCES "User" ("id") ON DELETE SET NULL ON UPDATE CASCADE
);
